import { GET_PHOTOS_REQUEST, GET_PHOTOS_SUCCESS } from "../actions/PageActions";

const initialState = {
  year: 2021,
  photos: [],
  isFetching: false,
};

export function pageReducer(state = initialState, action) {
  switch(action.type) {
    case SET_YEAR:
      return { ...state, year: action.payload }
    default:
      return state;
  }
};
